import { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import axios from 'axios';

const reducer = (state, action) => {
  switch (action.type) {
    case "FETCH_REQUEST":
      return { ...state, loading: true };
    case "FETCH_SUCCESS":
      return { ...state, products: action.payload,aisles: action.payload, loading: false };
    case "FETCH_FAIL":
      return { ...state, loading: false, error: action.payload };
    default:
      return state;
  }
};

function HomePage() {

    const [products, setProducts]=useState([]);
    useEffect(()=>{
     const fetchProd=async()=>{
         const returnProd = await axios.get('/api/products');
         setProducts(returnProd.data);
     };
     fetchProd();   
    },[]);

    const [aisles, setAisles] = useState([]);
        useEffect(() => {
          const fetchAisle= async () => {
            const returnAisle = await axios.get('/api/aisles');
            setAisles(returnAisle.data);
          };
          fetchAisle();
        }, []);

  return (
    <div>
      <h1>Featured Products</h1>
      <div className="fproducts">
        {products.map((product) => (
          <div className="fproducts">
            <div className="fproduct" key={product.slug}>
              <Link to={`/product/${product.slug}`}>
                <img src={product.image} alt={product.name} />
              </Link>
              <div className="fproduct-info">
                <Link to={`/product/${product.slug}`}>
                  <p>{product.name}</p>
                </Link>
                <p>
                  <strong>${product.price}</strong>
                </p>
                {/* <button>Add to cart</button> */}
              </div>
            </div>
          </div>
        ))}
      </div>

      <h1>Categories</h1>

      <div className="aisles">
        {aisles.map((aisles) => (
          <div className="aisles">
            <div className="aisle" key={aisles.slug}>
              <Link to={`/aisles/${aisles.slug}`}>
                <img src={aisles.image} alt={aisles.name}></img>
              </Link>
              <Link to={`/aisles/${aisles.slug}`}>
                <p>{aisles.name}</p>
              </Link>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
export default HomePage;
