import Axios from "axios";
import { Link, useLocation, useNavigate } from "react-router-dom";
import Container from "react-bootstrap/Container";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import { Helmet } from "react-helmet-async";
import { useContext, useEffect, useState } from "react";
import { Store } from "../Store";
import { getError } from "../utils";
import { toast } from "react-toastify";
import HCaptchaForm from "../components/HcaptchaForm";

export default function LoginPage() {
  const navigate = useNavigate();
  const { search } = useLocation();
  const redirectInUrl = new URLSearchParams(search).get("redirect");
  const redirect = redirectInUrl ? redirectInUrl : "/";

  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const { state, dispatch: ctxDispatch } = useContext(Store);
  const { userInfo } = state;
  const submitHandler = async (e) => {
    e.preventDefault();
    try {
      const { data } = await Axios.post("/api/users/login", {
        email,
        password,
      });
      console.log(data);
      ctxDispatch({ type: "USER_SIGNIN", payload: data });
      localStorage.setItem("userInfo", JSON.stringify(data));
      navigate(redirect || "/");
    } catch (err) {
      alert(getError(err));
    }
  };

  useEffect(() => {
    if (userInfo) {
      navigate(redirect);
    }
  }, [navigate, redirect, userInfo]);

  return (
    <Container fluid className="rounded-border-black-bg-small">
      <Helmet>
        <title>Sign In</title>
      </Helmet>
      <h1 className="my-3">Sign In</h1>
      <Form onSubmit={submitHandler}>
        <Form.Group className="mb-3" controlId="email">
          <Form.Label>Email</Form.Label>
          <Form.Control
            type="email"
            required
            onChange={(e) => setEmail(e.target.value)}
          />
        </Form.Group>
        <Form.Group className="mb-3" controlId="password">
          <Form.Label>Password</Form.Label>
          <Form.Control
            type="password"
            required
            onChange={(e) => setPassword(e.target.value)}
          />
        </Form.Group>
        <div className="mb-3">
          <div className="center-contents">
            <Button type="submit" variant="yellow-black-txt-wide">
              Sign In
            </Button>
          </div>
        </div>
        <HCaptchaForm></HCaptchaForm>
        <p>
          hCaptcha pending implementation, it works but has no effect of access
        </p>

        <div className="mb-3">
          <a href="https://alzheimer.ca/sites/default/files/documents/alzheimers-disease_getting-a-diagnosis_0.pdf" className="amzn-link">Forgot your password?</a>
          <br></br>
          New customer?{" "}
          <Link to={`/signup?redirect=${redirect}`} className="amzn-link ">
            Create your account
          </Link>
        </div>
      </Form>
      <br></br>
      <p>Customer demo: johndoe@dmail.com, psw:jd123456</p>
      <p>Admin demo: admin@darkriver.com, psw:123456 (under construction)</p>
      <p>seller menu pending implementation</p>
    </Container>
  );
}
