import axios from "axios";
import { useContext, useEffect, useReducer, useRef, useState } from "react";
import { Link, useNavigate, useParams } from "react-router-dom";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import Card from "react-bootstrap/Card";
import ListGroup from "react-bootstrap/ListGroup";
import Form from "react-bootstrap/Form";
import Badge from "react-bootstrap/Badge";
import Button from "react-bootstrap/Button";
import Rating from "../components/Rating";
import { Helmet } from "react-helmet-async";
import LoadingBox from "../components/LoadingBox";
import MessageBox from "../components/MessageBox";
import { getError } from "../utils";
import { Store } from "../Store";
import FloatingLabel from "react-bootstrap/FloatingLabel";
import { toast } from "react-toastify";
import { Container } from "react-bootstrap";
import { ListGroupItem } from "react-bootstrap";
import ratings_dist from "./ratings_dist.jpg";

const reducer = (state, action) => {
  switch (action.type) {
    case "REFRESH_PRODUCT":
      return { ...state, product: action.payload };
    case "CREATE_REQUEST":
      return { ...state, loadingCreateReview: true };
    case "CREATE_SUCCESS":
      return { ...state, loadingCreateReview: false };
    case "CREATE_FAIL":
      return { ...state, loadingCreateReview: false };
    case "FETCH_REQUEST":
      return { ...state, loading: true };
    case "FETCH_SUCCESS":
      return { ...state, product: action.payload, loading: false };
    case "FETCH_FAIL":
      return { ...state, loading: false, error: action.payload };
    default:
      return state;
  }
};

export default function ProductPage() {
  let reviewsRef = useRef();

  const [rating, setRating] = useState(0);
  const [comment, setComment] = useState("");
  const [selectedImage, setSelectedImage] = useState("");

  const navigate = useNavigate();
  const params = useParams();
  const { slug } = params;

  const [{ loading, error, product, loadingCreateReview }, dispatch] =
    useReducer(reducer, {
      product: [],
      loading: true,
      error: "",
    });
  useEffect(() => {
    const fetchData = async () => {
      dispatch({ type: "FETCH_REQUEST" });
      try {
        const result = await axios.get(`/api/products/slug/${slug}`);
        dispatch({ type: "FETCH_SUCCESS", payload: result.data });
      } catch (err) {
        dispatch({ type: "FETCH_FAIL", payload: getError(err) });
      }
    };
    fetchData();
  }, [slug]);

  const { state, dispatch: ctxDispatch } = useContext(Store);
  const { cart, userInfo } = state;
  const addToCartHandler = async () => {
    const existItem = cart.cartItems.find((x) => x._id === product._id);
    const quantity = existItem ? existItem.quantity + 1 : 1;
    const { data } = await axios.get(`/api/products/${product._id}`);
    if (data.countInStock < quantity) {
      window.alert("Sorry. Product is out of stock");
      return;
    }
    ctxDispatch({
      type: "CART_ADD_ITEM",
      payload: { ...product, quantity },
    });
    navigate("/cart");
  };

  const submitHandler = async (e) => {
    e.preventDefault();
    if (!comment || !rating) {
      toast.error("Please enter comment and rating");
      return;
    }
    try {
      const { data } = await axios.post(
        `/api/products/${product._id}/reviews`,
        { rating, comment, name: userInfo.name },
        {
          headers: { Authorization: `Bearer ${userInfo.token}` },
        }
      );

      dispatch({
        type: "CREATE_SUCCESS",
      });
      toast.success("Review submitted successfully");
      product.reviews.unshift(data.review);
      product.numReviews = data.numReviews;
      product.rating = data.rating;
      dispatch({ type: "REFRESH_PRODUCT", payload: product });
      window.scrollTo({
        behavior: "smooth",
        top: reviewsRef.current.offsetTop,
      });
    } catch (error) {
      toast.error(getError(error));
      dispatch({ type: "CREATE_FAIL" });
    }
  };
  return loading ? (
    <LoadingBox />
  ) : error ? (
    <MessageBox variant="danger">{error}</MessageBox>
  ) : (
    <Container fluid className="rounded-border-black-bg">
      <Row>
        <Col md={4}>
          <img
            className="img-large"
            src={selectedImage || product.image}
            alt={product.name}
          ></img>
          <p>image gallery pending implementation</p>
        </Col>
        <Col md={5}>
          <ListGroup variant="flush">
            <ListGroup.Item className="black-bg">
              <Helmet>
                <title>{product.name}</title>
              </Helmet>
              <h1>{product.name}</h1>
              <Link className="turquoise" to={`/seller/${product.brand}`}>
                <h6>Visit the {product.brand} store</h6>
              </Link>
            </ListGroup.Item>
            <ListGroup.Item className="black-bg">
              <div className="one-line-parent">
                <div className="one-line-child">
                  <Rating rating={product.rating}></Rating>{" "}
                </div>
                <div className="one-line-child">
                  <p className="turquoise">
                    &nbsp;{product.numReviews} ratings
                  </p>
                </div>
              </div>
            </ListGroup.Item>
            <ListGroup.Item className="black-bg">
              Price :<h3 className="colored-price">&nbsp;${product.price}</h3>
              <br></br> &amp;{" "}
              <a
                className="turquoise"
                href="https://www.amazon.com/gp/help/customer/display.html?nodeId=GNW5VKFXMF72FFMR"
              >
                {" "}
                Free Returns, most of the time{" "}
              </a>
            </ListGroup.Item>
            <ListGroup.Item className="black-bg">
              <p>option selector (ex:size) pending implementation</p>
            </ListGroup.Item>
            <ListGroup.Item className="black-bg">
              Description:
              <p>{product.description}</p>
            </ListGroup.Item>
          </ListGroup>
        </Col>
        <Col md={3}>
          <Card className="black-bg">
            <Card.Body className="black-bg">
              <ListGroup variant="flush">
                <ListGroup.Item className="black-bg">
                  <Row>
                    {" "}
                    <div className="one-line-parent">
                      <div className="one-line-child">
                        {" "}
                        <a
                          href="https://www.amazon.com/gp/help/customer/display.html?nodeId=GGE5X8EV7VNVTK6R"
                          className="turquoise"
                        >
                          FREE delivery&nbsp;
                        </a>
                      </div>
                      {product.price > 35 ? null : (
                        <>
                          {" "}
                          <div className="one-line-child"> on</div>
                          <br></br> orders above 35$
                        </>
                      )}
                    </div>
                  </Row>
                  <br></br>
                  <Row>
                    {" "}
                    <Col>Price:</Col>
                    <Col>${product.price}</Col>
                  </Row>
                </ListGroup.Item>
                <ListGroup.Item className="black-bg">
                  <Row>
                    <Col>
                      {product.countInStock > 0 ? (
                        <Badge bg="success">In Stock</Badge>
                      ) : (
                        <Badge bg="danger">Unavailable</Badge>
                      )}
                    </Col>
                  </Row>
                </ListGroup.Item>

                {product.countInStock > 0 && (
                  <ListGroup.Item className="black-bg">
                    <div className="d-grid">
                      <Button onClick={addToCartHandler} variant="amzn">
                        Add to Cart
                      </Button>
                    </div>
                  </ListGroup.Item>
                )}

                <ListGroupItem className="black-bg">
                  <div className="one-line-parent">
                    <div className="one-line-child">
                      <i className="fa fa-lock"></i>
                    </div>
                    <div className="one-line-child">
                      <a
                        href="https://www.amazon.com/gp/help/customer/display.html?nodeId=201909010"
                        className="turquoise"
                      >
                        &nbsp; Secure transaction
                      </a>
                    </div>
                  </div>
                </ListGroupItem>
                <ListGroupItem className="black-bg">
                  <div className="one-line-parent">
                    <div className="one-line-child">Sold by &nbsp;</div>
                    <div className="one-line-child">
                      {" "}
                      <Link
                        className="turquoise"
                        to={`/seller/${product.brand}`}
                      >
                        <h6>{product.brand}</h6>
                      </Link>{" "}
                    </div>
                  </div>
                  <div className="one-line-parent">
                    <div className="one-line-child">
                      and fulfilled by &nbsp;
                    </div>
                    <div className="one-line-child">
                      {" "}
                      <Link className="turquoise" to="/">
                        <h6>DarkRiver</h6>
                      </Link>{" "}
                    </div>
                  </div>
                </ListGroupItem>
              </ListGroup>
            </Card.Body>
          </Card>
        </Col>
      </Row>
      <Row>
        {" "}
        <Col md={4}>
          <div id="rating-distribution-temp">
            {" "}
            <h2 ref={reviewsRef}>Customer Reviews</h2>
            <p>
              review distribution pending implementation, this is just an image
            </p>
            <img src={ratings_dist} alt="rating distribution img"></img>
          </div>
        </Col>
        <Col md={8}>
          <div className="my-3">
            {userInfo ? (
              <form onSubmit={submitHandler}>
                <h3>Write a review</h3>
                <Form.Group className="mb-3" controlId="rating">
                  <Form.Label>Rating</Form.Label>
                  <Form.Select
                    aria-label="Rating"
                    value={rating}
                    onChange={(e) => setRating(e.target.value)}
                  >
                    <option value="">Select...</option>
                    <option value="1">1- Poor</option>
                    <option value="2">2- Fair</option>
                    <option value="3">3- Good</option>
                    <option value="4">4- Very good</option>
                    <option value="5">5- Excelent</option>
                  </Form.Select>
                </Form.Group>
                <FloatingLabel
                  controlId="floatingTextarea"
                  label="Comments"
                  className="mb-3"
                >
                  <Form.Control
                    as="textarea"
                    placeholder="Leave a comment here"
                    value={comment}
                    onChange={(e) => setComment(e.target.value)}
                  />
                </FloatingLabel>

                <div className="mb-3">
                  <Button disabled={loadingCreateReview} type="submit">
                    Submit
                  </Button>
                  {loadingCreateReview && <LoadingBox></LoadingBox>}
                </div>
              </form>
            ) : (
              <MessageBox>
                Please{" "}
                <Link to={`/login?redirect=/dr/${product.slug}`}>Sign In</Link>{" "}
                to write a review
              </MessageBox>
            )}
          </div>
          <div className="mb-3">
            {product.reviews.length === 0 && (
              <MessageBox>No reviews</MessageBox>
            )}
          </div>
          <ListGroup>
            {product.reviews.map((review) => (
              <ListGroup.Item key={review._id}>
                <strong>{review.name}</strong>
                <Rating rating={review.rating} caption=" "></Rating>
                <p>{review.createdAt.substring(0, 10)}</p>
                <p>{review.comment}</p>
              </ListGroup.Item>
            ))}
          </ListGroup>
        </Col>
      </Row>
    </Container>
  );
}
