import axios from "axios";
import { useContext, useEffect, useReducer } from "react";
import {
  Badge,
  Button,
  Card,
  Col,
  Container,
  ListGroup,
  Row,
} from "react-bootstrap";
import { useParams } from "react-router-dom";
import Rating from "../components/Rating";
import { Helmet } from "react-helmet-async";

const reducer = (state, action) => {
  switch (action.type) {
    case "FETCH_REQUEST":
      return { ...state, loading: true };
    case "FETCH_SUCCESS":
      return { ...state, product: action.payload, loading: false };
    case "FETCH_FAIL":
      return { ...state, loading: false, error: action.payload };
    default:
      return state;
  }
};

function ProductPage() {
  const params = useParams();
  const { slug } = params;

  const [{ loading, error, product }, dispatch] = useReducer(reducer, {
    product: [],
    loading: true,
    error: "",
  });
  useEffect(() => {
    const fetchData = async () => {
      dispatch({ type: "FETCH_REQUEST" });
      try {
        const result = await axios.get(`/api/products/slug/${slug}`);
        dispatch({ type: "FETCH_SUCCESS", payload: result.data });
      } catch (err) {
        dispatch({ type: "FETCH_FAIL", payload: err.message });
      }
    };
    fetchData();
  }, [slug]);

  const { state, dispatch: tba } = useContext(Store);
  const addToCartFunction = () => {
    tba({
      type: "CART_ADD_ITEM",
      payload: { ...product, quantity: 1 },
    });
  };

  return loading ? (
    <div>Loading...</div>
  ) : error ? (
    <div>{error}</div>
  ) : (
    <div className="rounded-border-black-bg">
      <Container fluid>
        <Row>
          <Col md={4} className="rounded-border-black-bg">
            <img
              className="img-large"
              src={product.image}
              alt={product.name}
            ></img>
          </Col>

          <Col md={5} className="rounded-border-black-bg">
            <ListGroup variant="flush">
              <ListGroup.Item className="black-bg">
                <Helmet>
                  <title>{product.name}</title>
                </Helmet>
                <p>Brand: {product.brand}</p>
                <h1 className="prod-txt">{product.name}</h1>
              </ListGroup.Item>
              <ListGroup.Item className="black-bg">
                <span>
                  <p className="one-line">
                    <Rating
                      rating={product.rating}
                      numRatings={product.numRatings}
                    ></Rating>
                  </p>
                  <p className="one-line">&nbsp;ratings</p>
                </span>
              </ListGroup.Item>
              <ListGroup.Item className="black-bg">
                <p className="one-line">Price : ${product.price}</p>
                <a
                  rel="noreferrer"
                  target="_blank"
                  className="one-line"
                  href="https://www.amazon.com/gp/help/customer/display.html?nodeId=202075130"
                  style={{ color: "#ffc000" }}
                >
                  &nbsp; &#38;Free Returns on some sizes and colours
                </a>
              </ListGroup.Item>
              <ListGroup.Item style={{ background: "black" }}>
                <p>
                  Description:<br></br>
                  {product.description}
                </p>
              </ListGroup.Item>
            </ListGroup>
          </Col>

          <Col md={3} className="rounded-border-black-bg">
            <Card className="black-bg">
              <Card.Body>
                <ListGroup variant="flush">
                  <ListGroup.Item className="black-bg">
                    <Row>
                      <Col>
                        <p>Price: </p>
                      </Col>
                      <Col>
                        <p>${product.price} </p>{" "}
                      </Col>
                    </Row>
                  </ListGroup.Item>
                  <ListGroup.Item className="black-bg">
                    <Row>
                      <Col>
                        <p>Status:</p>
                      </Col>
                      <Col>
                        {product.countInStock > 0 ? (
                          <Badge bg="success">In Stock</Badge>
                        ) : (
                          <Badge bg="danger">Unavailable</Badge>
                        )}
                      </Col>
                    </Row>
                  </ListGroup.Item>

                  {product.countInStock > 0 && (
                    <ListGroup.Item className="black-bg">
                      <div className="d-grid">
                        <Button variant="addCart" onClick={addToCartFunction}>
                          Add to Cart
                        </Button>
                      </div>
                    </ListGroup.Item>
                  )}
                </ListGroup>
              </Card.Body>
            </Card>
          </Col>
        </Row>
      </Container>
    </div>
  );
}
export default ProductPage;
