import axios from "axios";
import { useEffect, useReducer } from "react";
import {
  Badge,
  Button,
  Card,
  Col,
  Container,
  ListGroup,
  Row,
} from "react-bootstrap";
import { Link, useParams } from "react-router-dom";
import Rating from "../components/Rating";
import { Helmet } from "react-helmet-async";
import LoadingBox from "../components/LoadingBox";
import MessageBox from "../components/MessageBox";
import Aisle from "../components/Aisle";

function AislePage() {
  const params = useParams();
  const { slug } = params;
  const reducer = (state, action) => {
    switch (action.type) {
      case "FETCH_REQUEST":
        return { ...state, loading: true };
      case "FETCH_SUCCESS":
        return { ...state, product: action.payload, loading: false };
      case "FETCH_FAIL":
        return { ...state, loading: false, error: action.payload };
      default:
        return state;
    }
  };

  const [{ loading, error, aisle }, dispatch] = useReducer(reducer, {
    aisle: [],
    loading: true,
    error: "",
  });
  useEffect(() => {
    const fetchData = async () => {
      dispatch({ type: "FETCH_REQUEST" });
      try {
        const result = await axios.get(`/api/aisle/slug/${slug}`);
        dispatch({ type: "FETCH_SUCCESS", payload: result.data });
      } catch (err) {
        dispatch({ type: "FETCH_FAIL", payload: err.message });
      }
    };
    fetchData();
  }, [slug]);
  return (
    <>
      <Helmet>
        <title>{aisle.slug}</title>
      </Helmet>
      <Container fluid="xxl">
        <h1>Categories</h1>

        <div className="aisles">
          {loading ? (
            <LoadingBox />
          ) : error ? (
            <MessageBox variant="danger">{error}</MessageBox>
          ) : (
            <Row>
              {aisle.map((aisle) => (
                <Col key={aisle.slug} sm={5} md={5} lg={4} className="mb-3">
                  <Aisle aisle={aisle}></Aisle>
                </Col>
              ))}
            </Row>
          )}
        </div>
      </Container>
    </>
  );
}
export default AislePage;
