import { useEffect, useReducer, useState } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import logger from "use-reducer-logger";

const reducer = (state, action) => {
  switch (action.type) {
    case "FETCH_REQUEST":
      return { ...state, loading: true };
    case "FETCH_SUCCESS":
      return {
        ...state,
        products: action.payload,
        aisles: action.payload,
        loading: false,
      };
    case "FETCH_FAIL":
      return { ...state, loading: false, error: action.payload };
    default:
      return state;
  }
};

function HomePage() {
  const [{ loading, error, products }, dispatch] = useReducer(
    logger(reducer),
    {
      products: [],
      loading: true,
      error: "",
    }
  );

  //const [products, setProducts]=useState([]);
  useEffect(() => {
    const fetchProd = async () => {
      dispatch({ type: "FETCH_REQUEST" });
      try {
        const result = await axios.get("/api/products");
        dispatch({ type: "FETCH_SUCCESS", payload: result.data });
      } catch (err) {
        dispatch({ type: "FETCH_FAIL", payload: err.message });
      }
    };
    fetchProd();

     const fetchAisle = async () => {
       dispatch({ type: "FETCH_REQUEST" });
       try {
         const result = await axios.get("/api/aisles");
         dispatch({ type: "FETCH_SUCCESS", payload: result.data });
       } catch (err) {
         dispatch({ type: "FETCH_FAIL", payload: err.message });
       }
     };
     fetchAisle();
  }, []);



  return (
    <div>
      <h1>Featured Products</h1>
      <div className="fproducts">
        {products.map((product) => (
          <div className="fproducts">
            <div className="fproduct" key={product.slug}>
              <Link to={`/product/${product.slug}`}>
                <img src={product.image} alt={product.name} />
              </Link>
              <div className="fproduct-info">
                <Link to={`/product/${product.slug}`}>
                  <p>{product.name}</p>
                </Link>
                <p>
                  <strong>${product.price}</strong>
                </p>
                {/* <button>Add to cart</button> */}
              </div>
            </div>
          </div>
        ))}
      </div>

      <h1>Categories</h1>

      <div className="aisles">
        {aisles.map((aisles) => (
          <div className="aisles">
            <div className="aisle" key={aisles.slug}>
              <Link to={`/aisles/${aisles.slug}`}>
                <img src={aisles.image} alt={aisles.name}></img>
              </Link>
              <Link to={`/aisles/${aisles.slug}`}>
                <p>{aisles.name}</p>
              </Link>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
export default HomePage;
