import { useEffect, useReducer, useState } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import logger from "use-reducer-logger";

const reducer = (state, action) => {
  switch (action.type) {
    case "FETCH_REQUEST":
      return { ...state, loading: true };
    case "FETCH_SUCCESS":
      return {
        ...state,
        products: action.payload,
        aisles: action.payload,
        loading: false,
      };
    case "FETCH_FAIL":
      return { ...state, loading: false, error: action.payload };
    default:
      return state;
  }
};

function HomePage() {
  const [{ loading0, error0, products }, dispatch0] = useReducer(
    logger(reducer),
    {
      products: [],
      loading: true,
      error: "",
    }
  );

    const [{ loading1, error1, aisles }, dispatch1] = useReducer(
      logger(reducer),
      {
        aisles: [],
        loading: true,
        error: "",
      }
    );

  //const [products, setProducts]=useState([]);
  useEffect(() => {
    const fetchProd = async () => {
      dispatch0({ type: "FETCH_REQUEST" });
      try {
        const result = await axios.get("/api/products");
        dispatch0({ type: "FETCH_SUCCESS", payload: result.data });
      } catch (err) {
        dispatch0({ type: "FETCH_FAIL", payload: err.message });
      }
    };
    fetchProd();

     const fetchAisle = async () => {
       dispatch1({ type: "FETCH_REQUEST" });
       try {
         const result = await axios.get("/api/aisles");
         dispatch1({ type: "FETCH_SUCCESS", payload: result.data });
       } catch (err) {
         dispatch1({ type: "FETCH_FAIL", payload: err.message });
       }
     };
     fetchAisle();
  }, []);



  return (
    <div>
      <h1>Featured Products</h1>
      <div className="fproducts">
        {loading0 ? (
          <div>Loading...</div>
        ) : error0 ? (
          <div>{error0}</div>
        ) : (
          <Row>
            {products.map((product) => (
        <Col></Col>
            ))}
          </Row>
        )}
      </div>

      <h1>Categories</h1>

      <div className="aisles">
        {loading1 ? (
          <div>Loading...</div>
        ) : error1 ? (
          <div>{error1}</div>
        ) : (
          aisles.map((aisles) => (
            <div className="aisles">
              <div className="aisle" key={aisles.slug}>
                <Link to={`/aisles/${aisles.slug}`}>
                  <img src={aisles.image} alt={aisles.name}></img>
                </Link>
                <Link to={`/aisles/${aisles.slug}`}>
                  <p>{aisles.name}</p>
                </Link>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
}
export default HomePage;
