import { Badge, Container, Nav, Navbar } from "react-bootstrap";
import { BrowserRouter, Routes, Route, Link } from "react-router-dom";
import AislePage from "./screens/AislePage";
import HomePage from "./screens/HomePage";
import ProductPage from "./screens/ProductPage";
import { LinkContainer } from "react-router-bootstrap";
import { Store } from "./Store";
import { useContext } from "react";
function App() {
       const { state, dispatch: tba } = useContext(Store);
  const { cart } = state;


    const addToCartFunction = async () => {
      const existItem = cart.cartItems.find((x) => x._id === product._id);
      const quantity = existItem ? existItem.quantity + 1 : 1;
      const { data } = await axios.get(`/api/products/${product._id}`);
      if (data.countInStock < quantity) {
        window.alert("Sorry. Product is out of stock");
        return;
      }
      tba({
        type: "CART_ADD_ITEM",
        payload: { ...product, quantity },
      });}

  return (
    <>
      <BrowserRouter>
        <div className="d-flex flex-column site-container">
          <header>
            <Navbar bg="dark" variant="dark" className="topbar">
              <Container fluid>
                <LinkContainer style={{ marginLeft: "1rem" }} to="/">
                  <Navbar.Brand>
                    <span className="logo">
                      <p>D</p>
                      <i className="fa-brands fa-amazon" />
                      <p>rkRiver</p>
                    </span>
                  </Navbar.Brand>
                </LinkContainer>
                <Nav className="me-auto">
                  <Link to="/cart" className="nav-link">
                    <i className="fa fa-shopping-cart" aria-hidden="true"></i>
                    {cart.cartItems.length > 0 && (
                      <Badge pill bg="warning">
                        {cart.cartItems.reduce((a, c) => a + c.quantity, 0)}
                      </Badge>
                    )}
                  </Link>
                </Nav>
              </Container>
            </Navbar>
          </header>
          <main>
            <Container>
              <Routes>
                <Route path="/" element={<HomePage />} />
                <Route path="/product/:slug" element={<ProductPage />} />
                <Route path="/aisles/:slug" element={<AislePage />} />
              </Routes>
            </Container>
          </main>
          <footer>
            <div className="text-center">&#169; None but don't tell Amazon</div>
          </footer>
        </div>
      </BrowserRouter>
    </>
  );
}

export default App;
