import { useParams } from "react-router-dom";


function AislePage() {
  const params = useParams();
  const { slug } = params;
const reducer = (state, action) => {
  switch (action.type) {
    case "FETCH_REQUEST":
      return { ...state, loading: true };
    case "FETCH_SUCCESS":
      return { ...state, product: action.payload, loading: false };
    case "FETCH_FAIL":
      return { ...state, loading: false, error: action.payload };
    default:
      return state;
  }
};

  const [{ loading, error, aisle }, dispatch] = useReducer(reducer, {
    aisle: [],
    loading: true,
    error: "",
  });
  useEffect(() => {
    const fetchData = async () => {
      dispatch({ type: "FETCH_REQUEST" });
      try {
        const result = await axios.get(`/api/aisle/slug/${slug}`);
        dispatch({ type: "FETCH_SUCCESS", payload: result.data });
      } catch (err) {
        dispatch({ type: "FETCH_FAIL", payload: err.message });
      }
    };
    fetchData();
  }, [slug]);
  return (
    <>
      <Helmet>
        <title>{aisles.slug}</title>
      </Helmet>
      <Container fluid="xxl">
        <h1>Categories</h1>

        <div className="aisles">
          {loading1 ? (
            <LoadingBox />
          ) : error1 ? (
            <MessageBox variant="danger">{error1}</MessageBox>
          ) : (
            <Row>
              {aisles.map((aisle) => (
                <Col key={aisle.slug} sm={5} md={5} lg={4} className="mb-3">
                  <Aisle aisle={aisle}></Aisle>
                </Col>
              ))}
            </Row>
          )}
        </div>
      </Container>
    </>
  );
}
export default AislePage;
